CountryFact.create ([ { country_id: 1, fact_1: "Brazil has been working to close the gender gap for the past two decades, with initiatives like CiÃªncia sem Fronteiras, or the Brazil Scientific Mobility Program, to send 75,000 Brazilian students abroad for STEM education.", source_1: "http://mashable.com/2016/01/22/women-in-stem-global/#r88R.76r_uqX",
  fact_2: "Between 2011-2015, 49% of scientific scholarly articles were written by women.", source_2: "https://www.forbes.com/sites/shannonsims/2017/03/08/surprising-new-study-brazil-now-a-global-leader-in-gender-equality-in-science/#d6d79376f448",
  fact_3: "However, 1 in 4 women in Brazil typically leave the STEM field approximately 10 years into their career.", source_3: "http://www.catalyst.org/knowledge/women-science-technology-engineering-and-mathematics-stem",
  fact_4: "There are also cultural narratives the help keep women out of STEM. 'Girls play at home with dolls, and boys play with video games.' -Iana Chan", source_4: "http://www.americasquarterly.org/content/women-brazil-are-changing-world-tech-heres-how"},
  { country_id: 2, fact_1: "In general, women in China have disadvantages finding job opportunities due to having more family duties and expectations from society.", source_1: "N/A",
    fact_2: "In a 2015 study by the L'Oreal Foundation, it was found that in China 93% believe that women do not hold the necessary skillset to become scientists", source_2: "https://www.theguardian.com/women-in-leadership/2015/sep/24/67-of-europeans-dont-believe-women-have-the-skills-to-be-scientists",
    fact_3: "The government developed the 'Outline for the Development of Chinese Women 2011-2020' in attempt to raise the number of women in STEM to 35%", source_3: "http://www.scidev.net/global/capacity-building/news/china-aims-to-boost-number-of-women-scientists.html",
    fact_4: "The Chinese Young Women in Science Award was created in 2004 to help recognize the role of women in science and technology. So far, it has awarded 115 women.", source_4: "http://english.cast.org.cn/n214241/c353527/content.html"},
  { country_id: 3, fact_1: "Costa Rica is the first Central American country to pilot the TeachHer initiative to encourage girls and women in STEM fiels.", source_1: "http://www.ticotimes.net/2016/06/19/jill-biden-costa-rica-leader-gender-equality",
    fact_2: "The number of women graduating from universities has increased, and many women in Costa Rica have access to education, however, this hasn't translated into including women in the workforce. This may be because gender roles for men and women are traditional, with men expected to be the provider while women spend their week taking care of the household.", source_2: "http://www.ticotimes.net/2017/01/16/cynthia-castro-costa-rican-psychologist-fighting-gender-equality-global-level",
    fact_3: "The INAMU is working with municipalities and the private sector in Costa Rica to address issues of gender gaps and gender violence.", source_3: "http://www.ticotimes.net/2017/01/16/cynthia-castro-costa-rican-psychologist-fighting-gender-equality-global-level",
    fact_4: "In Central America, women employ 44% of all science and social science research positions, but continue to be underrepresented in STEM.", source_4: "https://www.reuters.com/article/us-latam-women-science-tech/latin-american-girls-hack-mans-world-of-tech-science-idUSKBN16E250"},
  { country_id: 4, fact_1: "2017 is the 'Year of Egyptian Women' according to President Abdel Fattah Al-Sisi, highlighting their central role for the future of Egypt and the need protect their constitutional rights.", source_1: "https://dailynewsegypt.com/2017/04/10/621604/",
    fact_2: "The female workforce in Egypt is 35%.", source_2: "https://dailynewsegypt.com/2017/04/10/621604/",
    fact_3: "Men are represented 3 times more than women in the workforce.", source_3: "https://www.al-monitor.com/pulse/fr/originals/2015/11/egypt-women-participation-labor-market.html",
    fact_4: "Egyptian women have a limited ability to work due to balancing career and family, unwillingness to hire a woman who has a child, gender discrimination in the workforce, and harassment.", source_4: "https://www.al-monitor.com/pulse/fr/originals/2015/11/egypt-women-participation-labor-market.html"},
  { country_id: 5, fact_1: "Germany has over 35,000 vacant engineering jobs. The Association of German Engineers is working to fill these positions with women.", source_1: "http://www.spiegel.de/international/germany/germany-boosts-efforts-to-get-women-in-stem-fields-a-924023.html",
